mapper.select:
select co_facility_id as commonFacility
from acco_common_facilities
where acco_id = #{value}

select image_file_name as image
from acco_detail_images
where acco_id = #{value}

select acco_tag as tag
from acco_tags
where acco_id = #{value}

select acco_type_id as type
from acco_types
where acco_id = #{value}

select ro_facility_id as roomFacility
from acco_room_facilities
where room_no = #{value}

select room_image_file_name as image
from acco_room_detail_images
where room_no = #{value}

select co_facility_id as id, co_facility_name as name, co_facility_icon_name as iconName from common_facilities where co_facility_id in (select co_facility_id from acco_type_cofa
acco_type_id in
#{type}
)

select restaurant_no as restaurantNo, restaurant_cat_id as category
from restaurant_cat
where restaurant_no = #{value}

select restaurant_no as restaurantNo, restaurant_tag as tag
from restaurant_tag
where restaurant_no = #{value}

select restaurant_no as restaurantNo, restaurant_menu_name as menuName, restaurant_price as price
from restaurant_menu
where restaurant_no = #{value}

mapper.update:
update accommodations set acco_name = #{name}, acco_district = #{district}, acco_address = #{address}, acco_latitude = #{latitude}, acco_longitude = #{longitude},
acco_thumbnail_image = #{thumbnailImageName},
acco_updated_date = sysdate, acco_deleted = #{deleted}, city_id = #{cityId}, acco_introduce_comment = #{introduceComment}, acco_detail_description = #{detailDescription}, acco_admin_id = #{adminId} where acco_id = #{id}

update acco_rooms set room_name = #{name}, room_numbers = #{numbers}, room_capacity = #{capacity}, room_day_price = #{dayPrice},
room_thumbnail_image = #{thumbnailImageName},
room_description = #{description} where room_no = #{no}

update restaurant set restaurant_name = #{name}, restaurant_location = #{location},
restaurant_imgname = #{imgname},
restaurant_tel = #{tel}, restaurant_latitud = #{latitud}, restaurant_longitude = #{longitude}, city_id = #{cityId}, restaurant_opening = #{opening}, restaurant_break_time = #{breakTime}, restaurant_close = #{close}, restaurant_district = #{district} where restaurant_no = #{no}

mapper.delete:
		delete
			acco_tags
		where
            acco_id = #{id}
        and
			acco_tag = #{deleteTag}

delete
	acco_room_detail_images
where
	room_no = #{no}
and
	room_image_file_name = #{deleteRoomDetailImgNames}

		delete
			restaurant_tag
		where
            restaurant_no = #{no}
        and
			restaurant_tag = #{deleteTag}

		delete
			restaurant_menu
		where
            restaurant_no = #{no}
        and
			restaurant_menu_name = #{deletMenuName}
		and
			restaurant_price = #{deleteMenuPrice}
