mapper.select:
select restaurant_tag
from restaurant_tag
where restaurant_no = #{value}

select * from (select r.review_title, r.review_content, a.acco_id, a.acco_name from review r, accommodations a, cities c where r.acco_id = a.acco_id and a.city_id = c.city_id and r.acco_id in
#{accoId}
order by r.review_created_date) where rownum <= 10
