mapper.select:
select user_id
from users
where user_name = #{name} 
and user_email = #{email}

mapper.delete:
delete from users 
where user_id = #{id}
and user_password = #{password}

mapper.select:
select count(user_id)
from users
where user_id = #{id}
and user_email =#{email}

mapper.update:
update users
set 
   user_password = #{password}
where user_email = #{email}
